<?php /* Template Name: Edit Department */ ?><?php get_header(); global $wpdb;			$table_name = $wpdb->prefix . "custom_department";$id = $_GET['id'];	 $query = "SELECT * FROM $table_name " ."WHERE id=$id";$projects = $wpdb->get_results("SELECT * FROM {$table_name}");$results_edit = $wpdb->get_row($query);?><div class="add_task">	<form action="" method="post" name="department" id="department">		<div class="section">			<div class="left">				<p class="label">Department Name</p>			</div>			<div class="right">				<input type="text" name="department_name" value="<?php echo (isset($results_edit->department_name)) ? $results_edit->department_name : '';  ?>"/>			</div>		</div>		<div class="section">			<div class="left">				<p class="label">Description</p>			</div>			<div class="right">				<textarea name="department_description" class="department_description textarea_wide"><?php echo (isset($results_edit->department_description)) ? $results_edit->department_description : '';  ?></textarea>			</div>		</div>		<input type="submit" name="submit" class="add_task button_1" value="Update" />		<a class="button_2" href="/add-deparment/">Cancel</a>	</form></div><?php	if(isset($_POST['submit'])):		$department_name			= $_POST['department_name'];	$department_description		= $_POST['department_description'];		$table_array = 	$update = $wpdb->update( $table_name , array( 	'department_name'			=> $department_name,	'department_description'	=> $department_description	),	array( 'ID' => $id ),	array( '%s', '%s' ));		if($update == 1):		echo "<p class='message'>";		echo "Project Updates!";	else:		echo "Project was not successfully Updated.";		echo "</p>";	endif;	endif;?><?php get_footer(); ?>