<?php /* Template Name: Client Actions */ ?><?php header("Location: /client/"); ?><?php global $wpdb;	$table_name = $wpdb->prefix . "custom_client";if(isset($_GET['archiveID'])) {	$archive_id = $_GET['archiveID'];	$update = $wpdb->update( $table_name , array( 	'client_status' => 0	),	array( 'ID' => $archive_id ),	array( '%s', '%s' ));}if(isset($_GET['unarchiveID'])) {	$archive_id = $_GET['unarchiveID'];		$update = $wpdb->update( $table_name , array( 	'client_status' => 1	),	array( 'ID' => $archive_id ),	array( '%s', '%s' ));}if(isset($_GET['deleteID'])) {	$id = $_GET['deleteID'];	$wpdb->query( "DELETE FROM {$table_name} WHERE ID = '$id'" ) ;}?>