<?php /* Template Name: Add Client Portal */ ?><?php get_header(); ?><?php$table_name_client = $wpdb->prefix . "custom_client";$client_names = $wpdb->get_col("SELECT DISTINCT client_name FROM {$table_name_client}");?><div class="add_client_portal">	<form action="" method="post" name="client_portal" id="client_portal">		<div class="section">			<div class="left">				<p class="label">Client: </p>			</div>			<div class="right">				<select name="client_portal_name" class="client_portal_name">					<?php 						$client_array = array();						foreach ($client_names as $client_name){							$client_array[] = $client_name;						}						sort($client_array);					?>					<?php foreach($client_array as $client){ ?>						<option><?php echo $client; ?></option>					<?php } ?>				</select>			</div>		</div>				<div class="section client_portal_inputs">			<div class="client_portal_container"></div>			<input type="text" name="client_portal_info" class="client_portal_info" placeholder="Info" />			<input type="text" name="client_portal_url" class="client_portal_url" placeholder="URL"  />			<input type="text" name="client_portal_login" class="client_portal_login" placeholder="Login"  />			<input type="text" name="client_portal_password" class="client_portal_password" placeholder="Password" />			<div class="button_2 add_more_client_portal">Add</div>		</div>				<input type="submit" name="submit" class="button_1 add_client_portal_button" value="Add Client Portal" />		<a class="button_2" href="/client-portal/">Cancel</a>	</form></div><?php get_footer(); ?>