oreach($goals as $goal){												if($goal->goal_type == 'Yearly' && $goal->goals_time == $year && $goal->goal_person != null){													if($goal->goal_person == $current_user_fullname){														$goal_person_detail = $wpdb->get_row("SELECT * FROM {$table_name_person} WHERE person_fullname = '$current_user_fullname'");														$person_id = $goal_person_detail->ID;														$goals_serialized = $goal->goals;														$goals_lists = unserialize($goals_serialized);														$goal_year = $goal->goal_year;														$goal_time = $goal->goals_time;														if($goals_lists != null){															foreach($goals_lists as $key => $goal){																$class = "personalyearly_".$goal_year."_".$goal_time."_".$key."_".$person_id;															?>															<li><input <?php echo (in_array($class, $current_personalyearly_goal_list_array)) ? "checked" : ""; ?> id="personal_yearly_goal_key_<?php echo $key; ?>" type="checkbox" class="goals_checkbox personal_yearly_goals" name="personal_yearly_goals" value="<?php echo $class; ?>"><?php echo $goal; ?></li>																											<?php															}														}													}												}											}										}									?>								</div>															</div>														<a class="button_1 dashboard_add_link" href="/add-goals/">Add Goals</a>							<div style="display: none;" class="goal_action_loader loader"></div>						</div>					</div>				</div>			</div>					</div>			</div>	<div class="full_width dashboard_column second_section">		<div class="two_third">			<div class="full_width">				<div class="one_half monthly_ongoing_progress">					<div class="data_container">						<div class="data_title">							<h3 class="title">Monthly Ongoing Progress</h3>													</div>						<div class="data_content">							<div class="header_titles">								<div class="first_column column"><p class="data_content_title">Customer</p></div>								<div class="second_column column"><p class="data_content_title">MPS</p></div>								<div class="third_column column"><p class="data_content_title">MPW</p></div>								<div class="fourth_column column"><p class="data_content_title">HDS</p></div>								<div class="fifth_column column"><p class="data_content_title">HDW</p></div>								<div class="sixth_column column"><p class="data_content_title">SAT</p></div>							</div>							<?php 							$filter_month = "STR_TO_DATE(date_now, '%d/%m/%Y') BETWEEN STR_TO_DATE('01/$month_number/$year', '%d/%m/%Y') AND STR_TO_DATE('31/$month_number/$year', '%d/%m/%Y')";							$filter_year = "STR_TO_DATE(date_now, '%d/%m/%Y') BETWEEN STR_TO_DATE('01/01/$year', '%d/%m/%Y') AND STR_TO_DATE('31/12/$year', '%d/%m/%Y')";							//$filter_month = "STR_TO_DATE(date_now, '%d/%m/%Y') BETWEEN STR_TO_DATE('01/09/$year', '%d/%m/%Y') AND STR_TO_DATE('31/09/$year', '%d/%m/%Y')";																$import_data_client = $wpdb->get_results("SELECT * FROM {$table_name_timesheet} WHERE $filter_month ORDER BY task_label ASC");								$task_client_name_array = array();								foreach ($import_data_client as $timesheet_data){									$task_client_name = $timesheet_data->task_label;									array_push($task_client_name_array,$task_client_name);								}								$client_names = array_unique($task_client_name_array);								$client_tab_total_hour = "";															$client_tab_total_billable_hour = "";								$mps_mpw_sort_array = array();																foreach($client_names as $client_name){									$monthly_plan = $wpdb->get_row("SELECT client_monthly_plan FROM {$table_name_client} WHERE client_name = '$client_name'");									$monthly_plan_name = $monthly_plan->client_monthly_plan;									$client_satisfaction_details = $wpdb->get_row("SELECT * FROM {$table_name_client} WHERE client_name = '$client_name'");																		$client_satisfaction = $client_satisfaction_details->client_satisfaction;									$client_satisfaction_id = $client_satisfaction_details->ID;									$mps = $wpdb->get_row("SELECT monthly_seo_hours FROM {$table_name_monthly_plan} WHERE monthly_name ='$monthly_plan_name'");									$mps_hour = $mps->monthly_seo_hours;									$mpw = $wpdb->get_row("SELECT monthly_webdev_hours FROM {$table_name_monthly_plan} WHERE monthly_name = '$monthly_plan_name'");																	$mpw_hour = $mpw->monthly_webdev_hours;									$timesheet_data = $wpdb->get_results("SELECT * FROM {$table_name_timesheet} WHERE $filter_month AND task_label = '$client_name'");									$array_count = count($timesheet_data);																		$total_dev_hours = "";									$seo_array = array();									$dev_array = array();									for($x = 0; $x <= $array_count; $x++){										$project_name = $timesheet_data[$x]->task_project_name;																					$task_client_name = $timesheet_data[$x]->task_label;										$task_hour = $timesheet_data[$x]->task_hour;										$task_name = format_task_name($timesheet_data[$x]->task_name);										$tasks = $wpdb->get_row("SELECT * FROM {$table_name_task} WHERE task_name='$task_name'");																				$project_data = $wpdb->get_row("SELECT * FROM {$table_name_project} WHERE project_name = '$project_name' AND project_client = '$task_client_name'");										if($project_data->project_invoice_method == 1){											if($tasks->task_billable == 1){												if($project_name == 'Monthly Ongoing SEO'){													$seo_array[$task_client_name][] = 'SEO_' . $task_hour;												}elseif($project_name == 'Monthly Ongoing Dev'){													$dev_array[$task_client_name][] = 'DEV_' . $task_hour;																				}												}										}									}																		if($seo_array != null){										foreach($seo_array as $seo_client => $seo_hours){											$total_seo_hours_decimal = "";											foreach($seo_hours as $seo_hour){												$seo_hour_explode = explode('_', $seo_hour);												$seo_task_hour_decimal = round(decimalHours($seo_hour_explode[1]), 2);												$total_seo_hours_decimal += $seo_task_hour_decimal;											}																				}									}else{										$total_seo_hours_decimal = '00:00';									}																											if($dev_array != null){										foreach($dev_array as $dev_client => $dev_hours){											$total_dev_hours_decimal = "";											foreach($dev_hours as $dev_hour){												$dev_hour_explode = explode('_', $dev_hour);												$dev_task_hour_decimal = round(decimalHours($dev_hour_explode[1]), 2);												$total_dev_hours_decimal += $dev_task_hour_decimal;											}																					}									}else{										$total_dev_hours_decimal = '00:00';									}																		$total_seo_hours = round_quarter($total_seo_hours_decimal);									$total_dev_hours = round_quarter($total_dev_hours_decimal);									$compare_decimal_seo = decimalHours($total_seo_hours);									$compare_decimal_dev = decimalHours($total_dev_hours);									$mps_value = ($mps_hour != null) ? $mps_hour : 0;									$mpw_value = ($mpw_hour != null) ? $mpw_hour : 0;																		$mps_mpw_sort_array[$mps_value ."_". $mpw_value][] = $client_name ."_". $compare_decimal_seo ."_". $total_seo_hours ."_". $compare_decimal_dev ."_". $total_dev_hours ."_". $client_satisfaction ."_". $client_satisfaction_id;								}								krsort($mps_mpw_sort_array, SORT_NATURAL);								foreach($mps_mpw_sort_array as $mps_value_mpw_value => $mps_mpw_sorting){																		$mps_mpw_explode = explode('_', $mps_value_mpw_value);									$mps_hour = $mps_mpw_explode[0];									$mpw_hour = $mps_mpw_explode[1];																		foreach($mps_mpw_sorting as $mps_mpw_sort){										$mps_mpw_sort_explode = explode('_', $mps_mpw_sort);										$client_name = $mps_mpw_sort_explode[0];										$compare_decimal_seo = $mps_mpw_sort_explode[1];										$total_seo_hours = $mps_mpw_sort_explode[2];										$compare_decimal_dev = $mps_mpw_sort_explode[3];										$total_dev_hours = $mps_mpw_sort_explode[4];										$client_satisfaction = $mps_mpw_sort_explode[5];										$client_satisfaction_id = $mps_mpw_sort_explode[6];										$client_satisfaction_class = str_replace(" ", "_", $client_satisfaction);										$client_satisfaction_class = strtolower($client_satisfaction_class);							?>										<div class='info_div'>											<div class="client_info first_column column"><li><?php echo $client_name; ?></li></div>											<div class="second_column column"><li><?php echo ($mps_hour != null ? $mps_hour : '-'); ?></li></div>											<div class="third_column column"><li><?php echo ($mpw_hour != null ? $mpw_hour : '-'); ?></li></div>											<div class="fourth_column column">												<li class="<?php echo ($mps_hour != null && $compare_decimal_seo >= $mps_hour && $compare_decimal_seo != '0' ? 'green_bg' : ''); ?>">													<?php echo ($total_seo_hours != "" ? $total_seo_hours : 0); ?>												</li>											</div>											<div class="fifth_column column">												<li class="<?php echo ($mpw_hour != null && $compare_decimal_dev >= $mpw_hour && $compare_decimal_seo != '0' ? 'green_bg' : ''); ?>">													<?php echo ($total_dev_hours != "" ? $total_dev_hours : 0); ?>												</li>											</div>											<div id="client_satisfaction_<?php echo $client_satisfaction_id; ?>" class="client_satisfaction_column sixth_column column"><li class="<?php echo $client_satisfaction_class; ?>"></li>												<div style="display:none" id="client_satisfaction_note_<?php echo $client_satisfaction_id; ?>" class="client_satisfaction_note <?php echo $client_satisfaction_class; ?>"><p><?php echo $client_satisfaction; ?></p></div>											</div>																		</div>														<?php 																		}								}							?>						</div>						<p>*MPS=Monthly Plan SEO, MPW=Monthly Plan Web</p>						<p>*HDS=Hours Done SEO, HDW=Hours Done Web</p>						<p>*SAT - Customer satisfaction</p>					</div>				</div>				<div class="one_half last active_projects">					<div class="data_container">						<div class="data_title"><h3 class="title">Active Webdev Projects</h3></div>						<div class="data_content">							<div class="header_titles">								<div class="first_column column"><p class="data_content_title">Customer</p></div>								<div class="second_column column"><p class="data_content_title">Project Name</p></div>								<div class="third_column column"><p class="data_content_title">Hours</p></div>								<div class="fourth_column column"><p class="data_content_title">Deadline</p></div>								<div class="fifth_column column"><p class="data_content_title">Status</p></div>							</div>							<?php 								$projects = $wpdb->get_results("SELECT * FROM {$table_name_project} ORDER BY project_client ASC");															foreach($projects as $project){																		if($project->project_status == 'unarchived'){																				$project_current_status = $project->project_current_status;										$project_estimated_deadline = $project->project_estimated_deadline;																		$project_client_name = $project->project_client;										$project_name = $project->project_name;										$project_start_date	= $project->project_start_date;											$start_date = date("d/m/Y", strtotime($project_start_date));										$today_date	= date('d/m/Y');											if($project_client_name != 'SEOWeb Solutions'){											if($project_name != 'Monthly Ongoing Dev' && $project_name != 'Monthly Ongoing SEO' && $project_name != 'Training' && $project_name !='Non working team' && $project_name !='Issue/Bug' ){												$timesheet_details = $wpdb->get_results("SELECT * FROM {$table_name_timesheet} WHERE task_label = '$project_client_name' AND task_project_name = '$project_name' AND STR_TO_DATE(date_now, '%d/%m/%Y') BETWEEN STR_TO_DATE('$start_date', '%d/%m/%Y') AND STR_TO_DATE('$today_date', '%d/%m/%Y')");												$total_project_hour = "";																						foreach($timesheet_details as $timesheet_detail){																							$task_hour = $timesheet_detail->task_hour;													$task_hour_decimal = round(decimalHours($task_hour), 2);													$total_project_hour += $task_hour_decimal;												}												$today = date('m/d/Y');												$today_date_ongoing_format = date("d/m/y", strtotime($today));												$today_date_ongoing_explode = explode('/', $today_date_ongoing_format);												$today_date_ongoing_mktime = mktime(0, 0, 0, $today_date_ongoing_explode[1], $today_date_ongoing_explode[0], $today_date_ongoing_explode[2]);																								$project_estimated_deadline_format = date("d/m/y", strtotime($project_estimated_deadline));												$project_estimated_deadline_explode = explode('/', $project_estimated_deadline_format);												$project_estimated_deadline_mktime = mktime(0, 0, 0, $project_estimated_deadline_explode[1], $project_estimated_deadline_explode[0], $project_estimated_deadline_explode[2]);							?>												<div class='info_div'>													<div class="client_info first_column column"><li><?php echo ($project_client_name != "" ? $project_client_name : '--'); ?></li></div>													<div class="second_column column"><li><?php echo $project_name; ?></li></div>													<div class="third_column column"><li><?php echo ($total_project_hour != "" ? round_quarter($total_project_hour) : 0); ?></li></div>													<div class="fourth_column column"><li class="<?php echo ($today_date_ongoing_mktime > $project_estimated_deadline_mktime) ? "red_bg" : ""; ?>"><?php echo $project_estimated_deadline; ?></li></div>													<div class="fifth_column column"><li><?php echo $project_current_status; ?></li></div>												</div>								<?php											}										}									}								}															?>											</div>					</div>					<div class="data_container customer_issues_bug">						<div class="data_title"><h3 class="title">Customer Issues/bugs</