me;?>		<div class="display_section delete_ajax_<?php echo $archived_client_id; ?>">					<div class="display_list" onclick="window.open('/client-information/?id=<?php echo $archived_client_id; ?>');">				<a class="button_2 display_button" href="<?php echo get_site_url(); ?>/edit-client/?editID=<?php echo $archived_client_id; ?>">Edit</a>				<h3 id="name_<?php echo $archived_client_id; ?>" class="display_subtitle"><?php echo $archived_client_name; ?></h3>							</div>				<div class="ajax_action_buttons">				<a class="button_2 display_button float_right" href="<?php echo get_site_url(); ?>/client-actions/?unarchiveID=<?php echo $archived_client_id; ?>">Unarchive</a>				<div id="delete_client_<?php echo $archived_client_id; ?>" class="button_2 display_button float_right delete_ajax">Delete</div>								<a class="button_2 display_button float_right confirm" href="<?php echo get_site_url(); ?>/manage-projects/websites/">Websites</a>						</div>			</div><?php } ?>	</div></div><div style="display:none;" class="dialog_form_delete_ajax" id="dialog_form_delete_client" title="Delete Client">	<form class="delete_action_ajax" id="delete_client">		<p class="label">			Are you sure you want to delete 			<span class="delete_name"></span>			<span style="display:none;" class="delete_prep">from</span>			<span style="display:none;" class="delete_parent"></span>		</p>			<div class="button_1 delete_button_ajax">Delete</div>		<div style="display:none" class="loader delete_ajax_loader"></div>		<input type="hidden" class="delete_id" name="delete_id" />		<input type="hidden" class="delete_type" name="delete_type" value="Client" />			</form></div><div style="display:none;" class="dialog_client_information" id="dialog_client_information" title="Client Information">	<div class="full_width">		<div class="one_half">			<p class="label">Customer Info:</p>			<p class="client_name"></p>			<p class="client_address"></p>			<p class="label">Contact Person:</p>			<p class="client_contact_person"></p>			<p class="client_contact_phone"></p>			<p class="client_contact_email"></p>		</div>		<div class="one_half last">			<div class="full_width">				<p class="label">Monthly Plan: </p>				<p class="client_monthly_plan"></p>			</div>			<div class="full_width">				<p class="label">Customer Satisfaction: </p>				<p class="client_satisfaction"></p>			</div>			<div class="full_width">				<p class="label">Current Active WebDev Projects: </p>				<p class="current_active_webdev_projects"></p>			</div>			<div class="full_width">				<p class="label">Monthly Ongoing Stat: </p>				<p class="monthly_ongoing_stat"></p>			</div>		</div>	</div>	<div class="full_width">		<h3>Customer Sites</h3>		<div class="header_titles">			<div class="first_column column">URL</div>			<div class="second_column column">Site Type</div>			<div class="third_column column">Platform</div>			<div class="fourth_column column">Version</div>			<div class="fifth_column column">Username</div>			<div class="sixth_column column">Password</div>			<div class="seventh_column column">L</div>		</div>		<div class="site_container"></div>	</div></div><?php get_footer(); ?>