<?php /* Template name: Timesheet Edit */ ?><?php get_header(); ?><?php 	$edit_id = $_GET['edit_id'];		$table_name = $wpdb->prefix . "custom_timesheet";	$table_name_person = $wpdb->prefix . "custom_person";?><?php if(isset($_POST['submit'])){	$task_name		= $_POST['task_name'];		$task_hour		= $_POST['task_hour'];	$task_label		= $_POST['task_label'];	$task_person	= $_POST['task_person'];			$table_array = 	$update = $wpdb->update( $table_name , array( 	'task_name'		=> $task_name,	'task_hour'		=> $task_hour,	'task_label'	=> $task_label,	'task_person'	=> $task_person	),	array( 'ID' => $edit_id ),	array( '%s', '%s' ));		if($update == 1){		echo "<p class='message'>";		echo "Updated!";		echo "</p>";	}else{		echo "<p class='message'>";		echo "Update Failed.";		echo "</p>";	}	}?><?php $results_edit = $wpdb->get_row("SELECT * FROM $table_name WHERE ID = $edit_id");$people = $wpdb->get_results("SELECT * FROM {$table_name_person}");?><div class="import_edit">	<form action="" method="post" name="" enctype="multipart/form-data" id="">		<div class="section">			<div class="left">				<p class="label">Task Name</p>			</div>			<div class="right">				<input type="text" class="task_name_edit" name="task_name" value="<?php echo (isset($results_edit->task_name)) ? $results_edit->task_name : '';  ?>"/>			</div>		</div>		<div class="section">			<div class="left">				<p class="label">Task Total Hours</p>			</div>			<div class="right">				<input type="text" class="task_hour_edit" name="task_hour" value="<?php echo (isset($results_edit->task_hour)) ? $results_edit->task_hour : '';  ?>"/>			</div>		</div>		<div class="section">			<div class="left">				<p class="label">Task Label</p>			</div>			<div class="right">				<input type="text" class="task_label_edit" name="task_label" value="<?php echo (isset($results_edit->task_label)) ? $results_edit->task_label : '';  ?>"/>			</div>		</div>		<div class="section">			<div class="left">				<p class="label">Task Responsible Person</p>			</div>			<div class="right">				<select class="task_person" name="task_person">					<option value="<?php echo (isset($results_edit->task_person)) ? $results_edit->task_person : '';  ?>"><?php echo $results_edit->task_person; ?></option>					<?php 						foreach($people as $person):						print_r($person);						if($person->person_first_name ." ". $person->person_last_name != $results_edit->task_person):					?>					<option><?php echo $person->person_first_name ." ". $person->person_last_name; ?></option>					<?php endif; endforeach; ?>				</select>			</div>		</div>		<input type="submit" name="submit" class="button_1 update_button" value="Update" />		<a class="button_2" href="/timesheet/">Cancel</a>	</form></div><?php get_footer(); ?>